import React from "react";
import { useContextGlobal } from "./utils/global.context";
import { Link } from "react-router-dom";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {
  
  const { state, dispatch } = useContextGlobal();

  const onHandleTheme = () => {
    const dark = {
      backgroundNavbar: "black",
      backgroundHome: "grey",
      color: "beige",
    };

    if (state.theme === "light") {
      dispatch({ type: "TOGGLE_THEME", payload: dark });
    } else {
      dispatch({ type: "TOGGLE_THEME", payload: "light" });
    }
  };

  return (
    <nav style={{background:state.theme.backgroundNavbar}} >
      <Link to={'/'}  style={{color:state.theme.color}}><h2>D<span>H</span> Odonto</h2></Link>
      <button>Change theme</button>
    </nav>
  );
};

export default Navbar;
