import React, { useContext } from 'react'
import { Context } from './utils/Context';
import { Link } from 'react-router-dom'

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

   const { state , handleNewColorTheme } =  useContext(Context)

   const style = {
    backgroundColor: state.theme === "light" ? "white" : "black",
    color: state.theme === "black" ? "white" : "white",
  };


  return (
    <nav style={style} >
      <Link to={'/home'}>Home</Link>
      <Link to={'/contact'}>Contact</Link>
      <Link to={'/Dentists/:id'}>Dentists</Link>
      <Link to={'/favorites'}>Favorites</Link>
      <button onClick={() => handleNewColorTheme()} >Change theme</button>
    </nav>
  )
}

export default Navbar
