import React, { useContext } from "react";
import medico from '../images/medico.png'
import { Link } from "react-router-dom";
import { Context } from "./utils/Context";

const Card = ({ data }) => {

const { handleNewFav } =  useContext(Context)

//TODO -> Agregar a favoritos
//TODO -> Modo oscuro

  return (
    <div className="cardContainer" >
      {
          data.map( item => (
            <Link key={item.id} to={`/dentists/${item.id}`}>
              <div className="card" >
                <img className="cardImage" src={medico} alt="medico" />
                <h1 className="cardH1">{ item.name }</h1>
                <p>{item.username}</p>
                <button className="favButton" onClick={ () => handleNewFav()}>Add fav</button>
              </div>
            </Link>
          ))
      }
  
    </div>
  );
};

export default Card;

{/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

{/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
