import { useReducer } from "react";
import { favReducer, reducer } from "./reducer";
import { useEffect } from "react";
import { Context } from "./Context";
import { json } from "react-router-dom";

//* InitialState *//

const initialState = {
  theme: "light",
  data: [],
  favs: [],
};

const init = () => {
  return JSON.parse(localStorage.getItem("favs")) || [];
};


export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [favs, dispatchFav] = useReducer(favReducer, initialState, init);

  const handleNewColorTheme = () => {
    const action = {
      type: "TOGGLE_THEME",
    };
    dispatch(action);
  };

  const handleNewFav = (fav) => {
    const action = {
      type: "ADD_FAVS",
      payload: fav,
    };

    dispatchFav(action);
  };

  const handleNewData = (fetchData ) => {
    const action = {
      type: "API_DATA",
      payload: fetchData
    };
  
    dispatch(action);
  };
  

  //*FetchData*//

  const fetchData = async () => {
    try {
      const resp = await fetch("https://jsonplaceholder.typicode.com/users");
      const jsonData = await resp.json();
      return jsonData;
    } catch (error) {
      console.error("Error al obtener los datos", error);
    }
  };

  //*Secondary Efects*//

  useEffect(() => {
    localStorage.setItem("favs", JSON.stringify(favs));
  }, [favs]);

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <Context.Provider
      value={{ state, handleNewColorTheme, handleNewFav, favs , fetchData }}
    >
      {children}
    </Context.Provider>
  );
};
