import React, { useContext, useState } from 'react'
import { Context } from './utils/Context';
import { Link } from 'react-router-dom'
import { BsFillMoonFill , BsFillSunFill} from "react-icons/bs";

const Navbar = () => {

   const { state , handleNewColorTheme } =  useContext(Context)
   const [icon, setIcon] = useState('moon')

   const style = {
    backgroundColor: state.theme === "light" ? "#e28743" : "#000000",
    color: state.theme === "light" ? "#000000" : "#FFFFFF",
  };

  const buttonStyle = {
    backgroundColor: icon === 'moon' ? '#000000' : '#FFFFFF'
  }

  
  return (
    <nav style={style} >
      <Link style={style} to={'/home'}>Home</Link>
      <Link style={style} to={'/contact'}>Contact</Link>
      <Link style={style} to={'/Dentists/:id'}>Dentists</Link>
      <Link style={style} to={'/favorites'}>Favorites</Link>
      <button onClick={() => {
        handleNewColorTheme()
        setIcon(icon === 'moon' ? 'sun' : 'moon')
        }} > { icon === 'moon' ? <BsFillSunFill/> : <BsFillMoonFill/> }</button>
    </nav>
  )
}

export default Navbar
