import { createContext, useReducer } from "react";
import { favReducer, reducer } from "./reducer";
import { useEffect } from "react";

//* InitialState *//

const initialState = {
  theme: "light",
  data: [],
  favs: []
};

const init = () => {
  return JSON.parse(localStorage.getItem('favs')) || [];
}


//* Context *//

export const ContextGlobal = createContext(undefined);


export const ContextProvider = ({ children }) => {

  //*Reducers*//

  const [state, dispatch] = useReducer(reducer, initialState);
  const [favs, dispatchFav] = useReducer(favReducer, initialState , init)

  //*Handlers*// 

  const handleNewColorTheme = () => {
    const action = {
      type: "TOGGLE_THEME",
    };
    dispatch(action);
  };

  const handleNewFav = ( fav ) => {
    const action = {
      type: "ADD_FAVS",
      payload : fav
    }
    dispatchFav(action)
  }

  //*FetchData*//

  const fetchData = async() => {
    try{
      const resp = await fetch('https://jsonplaceholder.typicode.com/users');
      const jsonData = await resp.json();
      const action = { type:'API_DATA' , payload: jsonData}
      dispatch(action);
    }catch(error){
      console.error("Error al obtener los datos" , error)
    }
  }

  //*Efectos Secundarios*//

  useEffect(() => {
    localStorage.setItem('favs' , JSON.stringify(favs))
  }, [favs])
  
  useEffect(() => {
    fetchData();
  }, [])
  
  
  return (
   <ContextGlobal.Provider value={{ state , handleNewColorTheme , handleNewFav , favs , fetchData}}>
     {children}
    </ContextGlobal.Provider>
  )
};
