export const reducer = ( initialState , action ) => {
    switch (action.type) {
        case "TOGGLE_THEME":
            return { ...initialState, theme: initialState.theme === 'light' ? 'dark' : 'light' };
        case "API_DATA":
            return initialState;
        default:
            return initialState;
    }
} 

export const favReducer = ( initialState, action) => {
    switch (action.type) {
      case "ADD_FAV":
        const newFavs = [...initialState.favs, action.payload];
        return {
          ...initialState,
          favs: newFavs,
        };
